mapper.update:
UPDATE TRANSACTION
payment_option=#{paymentOption}, receiver_name=#{receiverName}, receiver_phone=#{receiverPhone}, demailaddr=#{divyAddr}, dlvy_request=#{divyRequest}, dlvy_date=to_date(#{divyDate}, 'YY/MM/DD'), order_data=SYSDATE
WHERE tran_no=#{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT DISTINCT t.tran_no , t.prod_no, t.buyer_id , t.payment_option , t.receiver_name , t.receiver_phone, t.demailaddr , t.dlvy_request , t.tran_status_code , t.order_data, t.dlvy_date , p.prod_no AS p_pno, p.prod_name , p.prod_detail, p.manufacture_day, p.price , p.image_file, p.reg_date , u.user_id AS u_id, u.user_name , u.password , u.role , u.ssn, u.cell_phone , u.addr , u.email, u.reg_date AS u_rd FROM TRANSACTION t, product p, users u
t.prod_no = p.prod_no AND t.buyer_id = #{buyerId} AND t.buyer_id = u.user_id
ORDER BY t.tran_no ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}
